C program to input marks of five subject:
#include<stdio.h>
int main()
{
    float arr[5];
    float sum=0,avg;
    for(int i=0;i<5;i++)
    {
        scanf("%f",&arr[i]);
        sum+=arr[i];
    }
    avg=sum/5;
    printf("%.6f\n",sum);
    printf("%.6f\n",avg);
    printf("%.6f",avg);
}

