public class Palin{
    static void cal()
    {
        Scanner sc=new Scanner(System.in);
        int n=sc.nextInt();
        int temp=n,sum=0,r;
        while(n>0)
        {
            r=n%10;
            sum=(sum*10)+r;
            n=n/10;
        }
        if (temp==sum)
            System.out.println(temp+" is a palindrome number");
        else
            System.out.println(temp+" is not a palindrome number");
    }
