class Triangle 
 { 
    int length; 
    int breadth; 
    int width;
    Triangle (int l, int b, int w) 
    {  
        //Write your code here
        length=l;
        breadth=b;
        width=w;
    } 
    Triangle (Triangle obj) 
    { 
        //Write your code here
        this.length=obj.length;
        this.breadth=obj.breadth;
        this.width=obj.width;

    } 
    int perimeter() 
    { 
        //Write your code here
        return (length*breadth*width)/2;
    } 
} 
