import java.io.ByteArrayInputStream;
import java.util.*;
public class Main {
    public static void main(String args[])
    {
        Scanner sc=new Scanner(System.in);
        byte[] array = { 1, 2, 3, 4 };
        int n=sc.nextInt();
        try
        {
            ByteArrayInputStream input = new ByteArrayInputStream(array);
            input.skip(n);
            System.out.print("Input stream after skipping "+n+"  bytes: ");
            int data = input.read();
            while (data != -1)
            {
                System.out.print(data + ", ");
                data = input.read();
            }
            input.close();
        }
        catch (Exception e)
        {
            e.getStackTrace();
        }
    }
}

